package adt;public interface ListInterface<T> {    /** Remove all entries. */    void clear();    /** Append to the end. */    boolean add(T newEntry);    /** Insert at 1..n+1 (1-based). */    boolean add(int newPosition, T newEntry);    /** Remove and return entry at 1..n; return null if invalid. */    T remove(int givenPosition);    /** Replace entry at 1..n; return true if success. */    boolean replace(int givenPosition, T newEntry);    /** Get entry at 1..n; return null if invalid. */    T getEntry(int givenPosition);    /** Linear search (equals). */    boolean contains(T anEntry);    /** Current number of entries (n). */    int getNumberOfEntries();    /** Whether list is empty. */    boolean isEmpty();}